select.if:
SELECT b.*, m.nickname, c.cname, count(r.rno) as reply FROM BOARD b join MEMBER M on b.mno = m.mno left join CORPORATION c on c.cno = b.cno LEFT JOIN REPLY R ON B.bno = R.bno
WHERE b.cno = #{cno}
WHERE b.title like CONCAT('%', #{title},'%')
WHERE b.cno = #{cno} AND b.title like CONCAT('%', #{title},'%')
WHERE b.Cno IS NOT NULL
AND b.cno = #{cno}
AND b.title like CONCAT('%', #{title},'%')
AND b.cno = #{cno} AND b.title like CONCAT('%', #{title},'%')
WHERE b.Cno IS NULL
AND b.cno = #{cno}
AND b.title like CONCAT('%', #{title},'%')
AND b.cno = #{cno} and b.title like CONCAT('%', #{title},'%')
GROUP BY B.bno ORDER BY bno DESC LIMIT ${skip}, ${size};

SELECT b.*, m.nickname, c.cname, count(r.rno) as reply FROM BOARD b join MEMBER M on b.mno = m.mno left join CORPORATION c on c.cno = b.cno LEFT JOIN REPLY R ON B.bno = R.bno
WHERE b.cno = #{cno}
WHERE m.nickname like CONCAT('%', #{writer},'%')
WHERE b.cno = #{cno} AND m.nickname like CONCAT('%', #{writer},'%')
WHERE b.Cno IS NOT NULL
AND b.cno = #{cno}
AND m.nickname like CONCAT('%', #{writer},'%')
AND b.cno = #{cno} AND m.nickname like CONCAT('%', #{writer},'%')
WHERE b.Cno IS NULL
AND b.cno = #{cno}
AND m.nickname like CONCAT('%', #{writer},'%')
AND b.cno = #{cno} and m.nickname like CONCAT('%', #{writer},'%')
GROUP BY B.bno ORDER BY bno DESC LIMIT ${skip}, ${size};

mapper.insert:
