mapper.select:
select *
from price
where date <= date_format(now(), '%Y-%m-%d') and cno = #{cno}
and date >= date_add(date_format(now(), '%Y-%m-%d'), interval #{interval} month)
and date < date_add(date_add(date_format(now(), '%Y-%m-%d'), interval #{interval} month), interval 1 month)
order by date;
